function renderMatchData(roundNumber) {
    const matchList = document.getElementById("matchList");
    const roundData = matchData.find(data => data.round_number === roundNumber);

    if (roundData) {
        matchList.innerHTML = ""; 
        roundData.matches.forEach(match => {
            const matchItem = document.createElement("a");
            matchItem.href = "#";
            matchItem.classList.add("list-group-item", "list-group-item-action", "d-flex", "justify-content-between", "align-items-center");

            const matchDate = new Date(match.start_timestamp * 1000);  // 将时间戳转换为日期
            matchItem.innerHTML = `
                <div>
                    <div><strong>${match.home_team}</strong> vs <strong>${match.away_team}</strong></div>
                    <div><small>${matchDate.toLocaleDateString()} · ${matchDate.toLocaleTimeString()}</small></div>
                </div>
                <div><strong>${match.home_score} - ${match.away_score}</strong></div>
            `;
            matchList.appendChild(matchItem);
        });
    }
}

// 更新轮次按钮
function updateRoundButtons(currentRound) {
    const roundSelector = document.getElementById("roundSelector");
    const rounds = [];

    // 保持当前轮次按钮
    rounds.push({ round: currentRound, position: "center" });

    // 向左三角尖
    if (currentRound > 1) {
        rounds.unshift({ round: currentRound - 1, position: "left" });
    }

    // 向右三角尖
    if (currentRound < 38) {
        rounds.push({ round: currentRound + 1, position: "right" });
    }

    roundSelector.innerHTML = "";
    rounds.forEach(round => {
        const button = document.createElement("button");
        button.classList.add("btn", "btn-outline-light", "round-btn");
        button.dataset.round = round.round;

        if (round.position === "left") {
            button.innerHTML = `&#8592;`;  // 左三角尖
        } else if (round.position === "right") {
            button.innerHTML = `&#8594;`;  // 右三角尖
        } else {
            button.innerText = `Round ${round.round}`;  // 当前轮次
            button.addEventListener("click", () => {
                showRoundSelector(round.round); // 传递当前轮次
            });
        }

        button.addEventListener("click", () => {
            renderMatchData(round.round);
            updateRoundButtons(round.round);
        });
        roundSelector.appendChild(button);
    });
}

// 显示轮次选择器
function showRoundSelector(selectedRound) {
    const roundSelectorPopup = document.getElementById("roundSelectorPopup");
    const roundPopupBody = document.getElementById("roundPopupBody");

    roundPopupBody.innerHTML = "";

    for (let i = 1; i <= 38; i++) {
        const roundButton = document.createElement("button");
        roundButton.classList.add("btn", "btn-outline-light", "round-btn");
        roundButton.dataset.round = i;
        roundButton.innerText = `Round ${i}`;
        roundButton.addEventListener("click", () => {
            renderMatchData(i);
            updateRoundButtons(i);
            closeRoundSelector();
        });
        roundPopupBody.appendChild(roundButton);
    }

    roundSelectorPopup.style.display = "block";  // 显示选择框

    // 滚动到选中的轮次
    const selectedButton = Array.from(roundPopupBody.children).find(button => button.innerText === `Round ${selectedRound}`);
    if (selectedButton) {
        selectedButton.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
}

// 关闭轮次选择器
document.getElementById("closeRoundPopup").addEventListener("click", closeRoundSelector);

function closeRoundSelector() {
    document.getElementById("roundSelectorPopup").style.display = "none";  // 隐藏选择框
}

// 初始化轮次按钮
