document.querySelector('.btn-glass').addEventListener('click', function () {
    // 跳转到 start 区域
    document.getElementById("start").scrollIntoView({ behavior: 'smooth' });

    // 加载最新轮次的比赛数据
    renderMatchData(newestround_pl[0]); 
    updateRoundButtons(newestround_pl[0]); 
});





      
      
      
      







